DROP TABLE IF EXISTS `mscore`;
CREATE TABLE `mscore` (
  `msid` int(6) NOT NULL AUTO_INCREMENT,
  `Mname` varchar(20) DEFAULT NULL,
  `MAverage` int(11) DEFAULT NULL,
  `MMax` int(11) DEFAULT NULL,
