  `Myear` date DEFAULT NULL,
  `Mbatch` varchar(20) DEFAULT NULL,
  `MCompare` varchar(1024) DEFAULT NULL,
  `mstid` int(11) DEFAULT NULL,
  PRIMARY KEY (`msid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

